    function shuffleArray(a) {
        "use strict";
        var i, t, j;
        for (i = a.length - 1; i > 0; i -= 1) {
            t = a[i];
            j = Math.floor(Math.random() * (i + 1));
            a[i] = a[j];
            a[j] = t;
        }
        return a;
    }
    var program = function( context ) {
        context.beginPath();
        context.arc( 0, 0, 0.5, 0, PI2, true );
        context.fill();
    };

    /**
    * 3d framework initialization
    */
    this.init = function() {
        curName = MENU_INDEX;
        initSources();
        initThreeJS();
        maskGeometry();
    };
    this.render = function(r) {
        isRender = r;
    };
    this.start = function() {

        this.change(MENU_INDEX);
        addAddEventListeners();
        animate();
    };

    this.change = function(name) {
        this.completed = false;
        curName = name;

        canvasW = canvasSizeList[curName].w;
        canvasH = canvasSizeList[curName].h;

        changePolygon.apply(this,[name]);
        changeSlogan.apply(this,[name]);
    };

    this.resize = function(event) {
        windowHalfX = window.innerWidth/2;
        windowHalfY = window.innerHeight/2;
        updateRenderScreen();

    };

    function initThreeJS() {

        $("body").append('<div id="canvas"></div>');

        camera = new THREE.PerspectiveCamera(75, canvasW/canvasH, 1, 10000);
        camera.position.z = 50;
        scene = new THREE.Scene();
        group = new THREE.Group();
        renderer = new THREE.CanvasRenderer({alpha:true});/*window.WebGLRenderingContext ? new THREE.WebGLRenderer() : new THREE.CanvasRenderer();*/
        renderer.setSize(canvasW , canvasH);
        scene.add( group );
        $("#canvas")[0].appendChild(renderer.domElement);
        canvasElement = $("#canvas").find("canvas");
    }

    function maskGeometry() {

        var loader = new THREE.JSONLoader();
        // 메인  구
        var tmp = loader.parse( polygonSource[MENU_INDEX], "" );
        polygonObject[MENU_INDEX].geometry = tmp.geometry;
        polygonObject[MENU_INDEX].geometry_copy = tmp.geometry.clone();

        tmp = loader.parse( polygonSource[MENU_STORY], "" );
        polygonObject[MENU_STORY].geometry = tmp.geometry;
        polygonObject[MENU_STORY].geometry_copy = tmp.geometry.clone();

        tmp = loader.parse( polygonSource[MENU_PROGRAM], "" );
        polygonObject[MENU_PROGRAM].geometry = tmp.geometry;
        polygonObject[MENU_PROGRAM].geometry_copy = tmp.geometry.clone();

        tmp = loader.parse( polygonSource[MENU_STORE], "" );
        polygonObject[MENU_STORE].geometry = tmp.geometry;
        polygonObject[MENU_STORE].geometry_copy = tmp.geometry.clone();

        tmp = loader.parse( polygonSource[MENU_CNW], "" );
        polygonObject[MENU_CNW].geometry = tmp.geometry;
        polygonObject[MENU_CNW].geometry_copy = tmp.geometry.clone();

        tmp = loader.parse( polygonSource[MENU_COMMUNITY], "" );
        polygonObject[MENU_COMMUNITY].geometry = tmp.geometry;
        polygonObject[MENU_COMMUNITY].geometry_copy = tmp.geometry.clone();
    }

    function updateRenderScreen() {
        var scale = window.innerWidth/stageW;
        var cw = (cw>1) ? 0 : (canvasW)*scale;
        var ch = (ch>1) ? 0 : (canvasH)*scale;
        //cw = $(window).innerWidth();
        //ch = $(window).innerHeight();
        renderer.setSize(cw , ch);

        canvasElement.css({
            "width":cw,
            "height":ch,
            "margin":-(ch/2)+"px 0 0 "+(-cw/2)+"px"
        });

        if( camera ) {
            camera.updateProjectionMatrix();
            camera.aspect = canvasW / canvasH;
        }
        
        $(".main-slogan #index").css("width",$(window).outerWidth());
        $(".main-slogan #index img").css({
           "width":scale*100+"%",
           "height":scale*100+"%"
        });
        $(".main-slogan #index span").css({
           "width":350*scale+"px"
        });

        $(".main-slogan #index").css({
            "top":(canvasElement.offset().top + ((720*scale)/2 - (400/2*scale)))-$("#main_wrapper").position().top
        });
    }

    function animate(time) {
        requestAnimationFrame( animate );
        render();
    }

    function render() {
    	
        if( isRender==true ) {
            camera.position.x += ( mouse.x/40 - camera.position.x ) * 0.09;
            camera.position.y += ( -mouse.y/40 - camera.position.y ) * 0.09;
            camera.lookAt( scene.position );
            renderer.render( scene, camera );
        }
        if( curName==MENU_INDEX ){
            group.rotation.y -= 0.001;
            group.rotation.x -= 0.0001;
        }

        moveMenuSetPos();
    }

    function onDocumentMouseMove(event) {
        mouse.x = ( event.clientX - windowHalfX );
        mouse.y = ( event.clientY - windowHalfY );
    }

    function onDocumentMouseDown( event ){
        event.preventDefault();
        var $mouse = {x:0,y:0,z:1};
        var cp = canvasElement.position();
        $mouse.x = ( event.clientX / renderer.domElement.width ) * 2 - 1;
        $mouse.y = cp.top + -( event.clientY / renderer.domElement.height ) * 2 + 1;
        $mouse.x -= cp.left;
        $mouse.y -= cp.top;
        raycaster.setFromCamera( $mouse, camera );
        var intersects = raycaster.intersectObjects( group.children );
        if ( intersects.length > 0 ) {
            //console.log(intersects[ 0 ]);
            intersects[ 0 ].object.material.opacity = 0.5;
            //console.log(intersects[ 0 ].object.name);
        }
    }
    
    // 폴리곤 체인지
    function changePolygon(name) {

        var r_color = [ 0x42b3d6, 0x5288C5, 0x04f0ff, 0x0C9AF2, 0x1BCBF2, 0xafe5f5 ];
        var pscale = canvasSizeList[name].scale;
        var orgGemetroy;

        group.rotation.y = group.rotation.x = 0;

        if(polygon) {

            polygon.geometry = polygonObject[name].geometry;
            orgGemetroy = polygonObject[name].geometry_copy;
            polygon.scale.set(pscale, pscale, pscale);

        } else {

            particles = [];
            orgGemetroy = polygonObject[name].geometry_copy;
            polygon = new THREE.Mesh(polygonObject[name].geometry, wireframeMaterial);
            polygon.geometry.verticesNeedUpdate = true;
            polygon.scale.set(pscale, pscale, pscale);
            group.add( polygon );
        }

        var verticeslength = polygon.geometry.vertices.length;
        removeParticles();
        updateRenderScreen();

        //var tmpSpr = getSpriteDot(0xff0000);

        for( i=0; i<verticeslength; i++){
            polygon.geometry.vertices[i].x = 0;//Math.random()*1.1 - 1;
            polygon.geometry.vertices[i].y = 0;//Math.random()*0.1 - 1;
            polygon.geometry.vertices[i].z = 0;//Math.random()*0.1 - 1;

            var color = r_color[Math.ceil(Math.random()*6)-1];//(orgGemetroy.vertices[i].z > 0) ? 0xFFFFFF : 0x66c8f5;

             if(name==MENU_STORY) {
                if(i==12||i==18||/*i==45||*/i==75||/*i==117||*/i==95) {
                    color = 0xFFFFFF;
                }
            } else if(name==MENU_PROGRAM) {
                if(i==14||i==40||/*i==32||*/i==6){
                    color = 0xFFFFFF;
                }
            } else if(name==MENU_STORE) {
                if(i==43||i==40||i==103||i==50){
                    color = 0xFFFFFF;
                }
            } else if(name==MENU_CNW) {
                if(i==75||i==153||i==45){
                    color = 0xFFFFFF;
                }
            } else if(name==MENU_COMMUNITY) {
                if(i==9||i==32||i==14||i==36||i==26||i==2||i==22){
                    color = 0xFFFFFF;
                }
            }
            var p = getSpriteDot(color);
                //p.position.x = -100;//Math.random() *  1000 - 500;
                //p.position.y = -100;//Math.random() *  1000 - 450;
                p.position.z = -10;//Math.random() *  1000 - 450;
                p.name = i;
            var rans = (Math.random()*0.3+0.3).toFixed(3);

            // 각 메뉴에 위치한 파티클은 크기가 크다..
            if(name==MENU_STORY) {
                if(i==12||i==18||i==45||i==75||/*i==117||*/i==95) {
                    p.scale.multiplyScalar(1);
                } else {
                    p.scale.multiplyScalar(rans);
                    initPat(p);
                }
            } else if(name==MENU_PROGRAM) {
                if(i==14||i==40||i==32||i==6){
                    p.scale.multiplyScalar(1);
                } else{
                    p.scale.multiplyScalar(rans);
                    initPat(p);
                }
            } else if(name==MENU_STORE) {
                if(i==43||i==40||i==103||i==50){
                    p.scale.multiplyScalar(1);
                }else {
                    p.scale.multiplyScalar(rans);
                    initPat(p);
                }
            } else if(name==MENU_CNW) {
                if(i==75||i==153||i==45){
                    p.scale.multiplyScalar(1);
                } else {
                    p.scale.multiplyScalar(rans);
                    initPat(p);
                }
            } else if(name==MENU_COMMUNITY) {
                if(i==9||i==32||i==14||i==36||i==26||i==2||i==22){
                    p.scale.multiplyScalar(1);
                }else {
                    p.scale.multiplyScalar(rans);
                    initPat(p);
                }
            } else if(name==MENU_INDEX) {
                p.scale.multiplyScalar(rans);
                initPat(p);
            }


            //if(name!=MENU_INDEX) {
            if(Math.random()*100 <= 10) {
                polygon.geometry.vertices[i].x = orgGemetroy.vertices[i].x;
                polygon.geometry.vertices[i].y = orgGemetroy.vertices[i].y;
                polygon.geometry.vertices[i].z = orgGemetroy.vertices[i].z;
                updatePosition(p,polygon.geometry.vertices[i]);

            } else {

                TweenMax.to( polygon.geometry.vertices[i], 0.8, { delay:i*polygonDelay[name],
                                                                x:orgGemetroy.vertices[i].x,
                                                                y:orgGemetroy.vertices[i].y,
                                                                z:orgGemetroy.vertices[i].z,
                                                                ease:Power2.easeOut,onUpdate:updatePosition,
                                                                onUpdateParams:[p,polygon.geometry.vertices[i]]} );
            }
            group.add(p);
            particles.push(p);
        }
        var t = this;

        TweenMax.fromTo(group.rotation,2.5, {y:4},{y:0,ease:Power2.easeOut,onComplete:function(){
            t.completed = true;
        }});

        //t.completed = true;
        TweenMax.fromTo(group.scale,2, {x:0.01,y:0.01,z:0.01},{x:1,y:1,z:1,ease:Cubic.easeOut});

        function updatePosition(p, v) {
            p.position.x =  v.x*pscale;
            p.position.y =  v.y*pscale;
            p.position.z =  v.z*pscale;
        }
    }
    
    function initPat(p) {
        var s = p.scale.x;
        toBig();
        function toBig() {
            var d = Math.random()*3 - 2;
            if( d < 0 )
                d = 0.3;
            var tars = s+0.4;
            if(tars > 0.7 ) {
                tars = 0.7;
            }
            TweenMax.to(p.material,0.5,{delay:d,opacity:1,ease:Linear.easeOut,onComplete:toSmall});
            TweenMax.to(p.scale,0.5,{delay:d,x:tars,y:tars,ease:Linear.easeOut});
        }
        function toSmall() {
            var d2 = Math.random()*3 - 2;
            if( d2 <0 )
                d2 = 0.5;
            var tars = s-0.3;
            if( tars < 0.1 ) {
                tars = 0.1;
            }
            TweenMax.to(p.material,0.5,{delay:d2,opacity:0.2,ease:Linear.easeOut,onComplete:toBig});
            TweenMax.to(p.scale,0.5,{delay:d2,x:tars,y:tars,ease:Linear.easeOut});
        }
    }
    
    // 생성되어있던 파티클을 삭제.
    function removeParticles() {
        if(particles.length!=0) {
            for( var i=0; i<particles.length; i++ ) {
                TweenMax.killTweensOf(particles[i]);
                group.remove(particles[i]);
            }
            particles = [];
        }
    }
    function getSpriteDot(color) {
        return new THREE.Sprite( getBasicMaterial(color) );
    }

    function getBasicMaterial(color) {
        return new THREE.SpriteCanvasMaterial
        ({
            color:color,
            transparent: false,
            opacity: 1,
            program: program
        });
    }

    function moveMenuSetPos() {

        var length  = submenuArr.length;
        if( length < 1 ){
            return;
        }

        var scalex = canvasElement.width()/canvasW;
        var scaley = canvasElement.height()/canvasH;

        var overpos = createVector(curOver.x,curOver.y,curOver.z);
        var ox = (overpos.x + canvasElement.offset().left)-60;
        var oy = (overpos.y + canvasElement.offset().top)-60;
        $(".dot-over").css("transform","matrix(1, 0, 0, 1, "+ox+","+oy+")");

        for( var i=0; i<length; i++ ) {
            var cp = createVector(curSubmenuPos[i].x,curSubmenuPos[i].y,curSubmenuPos[i].z);
            var xx = (cp.x + canvasElement.offset().left + 40);
            var yy = (cp.y + canvasElement.offset().top - 40);
            $("#"+curName+"_"+i).css("transform","matrix(1, 0, 0, 1, "+xx+","+yy+")");
        }
    }

    function degInRad(deg) {
        return deg * Math.PI / 180;
    }

    function createVector(x, y, z) {
        var p = new THREE.Vector3(x, y, z);
        var vector = p.project(camera);
        vector.x = (vector.x + 1) / 2 * renderer.domElement.width;
        vector.y = -(vector.y - 1) / 2 * renderer.domElement.height;
        return vector;
    }

    function addAddEventListeners() {
        $(document).on( "mousemove", onDocumentMouseMove );
        $(document).on( "mousedown", onDocumentMouseDown );
    }
    
